create table inputs
(
    id                     varchar(30) not null
        constraint inputs_pk
            primary key,
    type                   varchar(50),
    caption                jsonb,
    required               boolean default false,
    placeholder            varchar(50),
    tooltip                varchar(100),
    default_value          varchar(100),
    have_static_data       boolean,
    refillable             boolean default false,
    refillable_source      varchar(50),
    updaters               jsonb,
    additional_regulations jsonb
);

comment on column inputs.have_static_data is 'ключ источника данных';

alter table inputs
    owner to postgres;

